package controlflow;

public class QuestionFour {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		  int x = 15;

	        if (x < 10 || x > 20) {
	            System.out.println("Out of range");
	        } else {
	            System.out.println("In range");
	        }

	        x = 5;

	        if (x < 10 || x > 20) {
	            System.out.println("Out of range");
	        } else {
	            System.out.println("In range");
	        }

	}

}
