package controlflow;

public class QuestionTwo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 7;

        if (x < 10) {
            System.out.println("Less than 10");
        } else {
            System.out.println("Greater than 10");
        }

        x = 15;

        if (x < 10) {
            System.out.println("Less than 10");
        } else {
            System.out.println("Greater than 10");
        }

	}

}
